import React, {useState, useEffect} from "react";
import {
  MDBBtn,
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBInput,
  MDBCheckbox,
  MDBRadio,
} from "mdb-react-ui-kit";
import "./onLineForm.css";
import "./core.css";
import {baseUrl} from '../../constant';

function OnLineForm() {
	const [inputField , setInputField] = useState({
		formValues: {}
	})
	const [validation, setValidation] = useState({inputField});

	useEffect(() => {
    	validateField();
  	}, [inputField]);

	const inputsHandler = (e) =>{
		console.log(e.target.name);
        let formValues = inputField.formValues;
		const {name, value} = e.target;

        formValues[name] = value;
		console.log(formValues);
        setInputField({formValues});
    }

    const submitButton = () =>{
		console.log("inputField: ",inputField);
        const requestOptions = {
			method: 'POST',
			headers: { 'Content-Type': 'application/json' },
			body: JSON.stringify(inputField.formValues)
		};
		fetch(`${baseUrl}enquiry/create`, requestOptions)
			.then(response => response.json())
			.then(data => console.log(data));
    }

	const validateField = (fieldName, value) => {
	  	let errors = validation;
		let formValues = inputField.formValues;
		if(Object.keys(formValues).length){
		if (!formValues.first_name.trim()) {
		  errors.first_name = "First Name is required";
		} else {
		  errors.first_name = "";
		}
		if (!formValues.last_name.trim()) {
		  errors.last_name = "Last Name is required";
		} else {
		  errors.last_name = "";
		}
		if (!formValues.mother_name.trim()) {
		  errors.mother_name = "Mother Name is required";
		} else {
		  errors.mother_name = "";
		}
		if (!formValues.father_name.trim()) {
		  errors.father_name = "Father Name is required";
		} else {
		  errors.father_name = "";
		}
		if (!formValues.family_income.trim()) {
		  errors.family_income = "Faily Income is required";
		} else {
		  errors.family_income = "";
		}
		if (!formValues.school_name.trim()) {
		  errors.school_name = "School Name is required";
		} else {
		  errors.school_name = "";
		}
		if (!formValues.board_name.trim()) {
		  errors.board_name = "Borad Name is required";
		} else {
		  errors.board_name = "";
		}
		if (!formValues.cast_category.trim()) {
		  errors.cast_category = "Category is required";
		} else {
		  errors.cast_category = "";
		}
		if (!formValues.address.trim()) {
		  errors.address = "Address is required";
		} else {
		  errors.address = "";
		}
		if (!formValues.city.trim()) {
		  errors.city = "City Name is required";
		} else {
		  errors.city = "";
		}
		if (!formValues.pin_code.trim()) {
		  errors.pin_code = "Address is required";
		} else {
		  errors.pin_code = "";
		}
		const emailCond =	"/^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:.[a-zA-Z0-9-]+)*$/";
		if (!formValues.email.trim()) {
		  errors.email = "Email is required";
		} else if (!formValues.email.match(emailCond)) {
		  errors.email = "Please enter a valid email address";
		} else {
		  errors.email = "";
		}
			setValidation(errors);
		}

	}

	let formValidationObject =  validation.inputField.formValues;
	console.log("validation", Object.keys(formValidationObject).length, formValidationObject);
	let formErrorValues = Object.keys(formValidationObject).length ? validation.formValues : '';
	console.log("formErrorValues", formErrorValues);

  return (
    <MDBContainer fluid className="h-custom">
      <MDBRow className="d-flex justify-content-center align-items-center h-100">
        <MDBCol col="12" className="m-5">
          <MDBCard
            className="card-registration card-registration-2"
            style={{ borderRadius: "15px" }}
          >
            <MDBCardBody className="p-0">
              <MDBRow>
                <MDBCol md="6" className="p-5 bg-white">
                  <h3 className="fw-normal mb-5">General Infomation</h3>

                  <MDBRow>
                    <MDBCol md="6">
                      <MDBInput
                        wrapperClass="mb-4"
                        label="First Name"
