import React, { Component } from "react";
import { Form } from "react-bootstrap";
import { Fade } from "react-reveal";
import html2canvas from 'html2canvas';
import jsPDF from 'jspdf';
import Select from "react-select";
import { useRef } from "react";
import Header from "../../components/header/Header";
import Footer from "../../components/footer/Footer";
import GithubRepoCard from "../../components/githubRepoCard/GithubRepoCard";
import AdmitCard from "../../containers/education/AdmitCard";
import PublicationCard from "../../components/publicationsCard/PublicationCard";
import Button from "../../components/button/Button";
import SelectBox from "../../components/Select/SelectBox";
import TopButton from "../../components/topButton/TopButton";
import {cityList, cityListWithLabel, enrollmentPrefix, enrollmentStatic} from "../../constant";
import {
  greeting,
  projectsHeader,
  publicationsHeader,
  publications,
  contactPageData,
  baseUrl,
} from "../../portfolio.js";
import ProjectsData from "../../shared/opensource/projects.json";
import "./Projects.css";
import ProjectsImg from "./ProjectsImg";

class AdmitCardPage extends Component {
constructor(props) {
    super(props);
    this.state = {
    admitStatus: false, 
    loading: false, 
    center: '', 
    selector: '',
    res: '', 
    resStatus:'',
    errorValues:'',
    buttonText:'Get Your Admit Card',
    };
    this.searchInput = React.createRef();
  }

  onChangeCenter = selectedOption => {
    this.setState({ center: selectedOption });
    console.log(`Option selected:`, selectedOption);
  };
  changeHandler = (e) => {
  	console.log(e.target.value);
  	this.setState({ selector: e.target.value });
  }

setVisibal = () => {
	let errors = {};

	if (!this.state.selector || !this.state.selector.trim()) {
	  	errors.selector = "Mobile Nmuber / Email is required";
	} 
	else {
	   	delete errors.selector;
	}
	if(!this.state.center){
		errors.center = "Please select your Exam Center";
	}else{
		delete errors.center;
	}
	const requestOptions = {
		method: 'GET',
	        headers: { 'Content-Type': 'application/json' }
	};
	const readyToCall = Object.keys(errors).length ;
	if(!readyToCall){
	errors= {};
	fetch(`http://localhost:3001/enquiry/getEnquiryData/${this.state.selector}`, this.state.selctor)
		.then(response => response.json())
		.then(data => {
			this.setState({ res:data,
			resStatus: data.statusType,
			errorValues : {}
			});
			errors= {};
			if(data.statusType !== 'error'){
				this.setState({admitStatus: true, buttonText: 'Download Your Admit Card'});
			}
		});


	}else{
		this.setState({errorValues: errors})
	}
};

 asyncPDF(data) {
 console.log(data);
  this.setState({buttonText: 'Downloading...'});
  this.printDocument(data).then(()=>{
  this.setState({buttonText: 'Get Your Admit Card'});
  const requestOptions = {
		method: 'PUT',
	        headers: { 'Content-Type': 'application/json' },
	        body: data.center
	};

	fetch(`http://localhost:3001/enquiry/updateCenter/${data.selector}`, data.selctor)
		.then(response => response.json())
		.then(data => {
			this.setState({ res:data,
			resStatus: data.statusType,
			errorValues : {}
			});
			if(data.statusType !== 'error'){
				this.setState({admitStatus: true, buttonText: 'Download Your Admit Card'});
			}
		});
});
}
  printDocument = (data) =>{
  return new Promise(resolve => {
    const input = document.getElementById('divToPrint');
    html2canvas(input, {useCORS: true}).then(function(canvas) {
      let img = new Image();
      img.src = canvas.toDataURL('image/png');
      img.onload = function () {
        let pdf = new jsPDF('l', 'px', 'junior-legal', true);
        pdf.addImage(img, 0, 0, pdf.internal.pageSize.width, pdf.internal.pageSize.height);
        pdf.save(`hallTicket-${enrollmentPrefix}${parseInt(enrollmentStatic)+parseInt(data.res.data.id)}.pdf`);
        setTimeout(()=>{
	    resolve('resolved');
    	},3000)
      };
    });
    this.setState({admitStatus: false, loading: false});
  });
  }
  render() {
  console.log( "console.log(e.target.value);",this.state );
    const theme = this.props.theme;
    return (
      <div className="projects-main">
        <Header theme={theme} />
        <div className="basic-projects">
          <Fade bottom duration={2000} distance="40px">
            <div className="projects-heading-div">
              <div className="projects-heading-img-div">
                <ProjectsImg theme={theme} />
              </div>
              <div className="projects-heading-text-div">
                <h1
                  className="projects-heading-text"
                  style={{ color: theme.text }}
                >
                  {projectsHeader.title}
                </h1>
                <p
                  className="projects-header-detail-text subTitle"
                  style={{ color: theme.secondaryText }}
                >
                  {projectsHeader["description"]}
                </p>
              </div>
            </div>
          </Fade>
        </div>

        <div className="project-button">
		<form>
			<Form.Group className="mb-3" controlId="formBasicPassword">

				<Form.Control type="text" className={
              this.state.selector !== '' ? " active" : ""
            } onChange={this.changeHandler} value={this.selector} />
				<Form.Label>Mobile Number / Email</Form.Label>
				<label className={this.state.errorValues.selector || this.state.resStatus === 'error' ? 'invalid-feedback': 'hide'}>{this.state.errorValues.selector ? this.state.errorValues.selector : this.state.resStatus === 'error' ? `No Record found with ${this.state.selector} please try with different Mobile Nmuber / Email is required.` : '' }</label>
				<div className="form-notch">
					<div className="form-notch-leading"></div>
					<div className="form-notch-middle" style={{width: "140px"}}>
					</div>
					<div className="form-notch-trailing"></div>
				</div>
		      	</Form.Group>	
			<Form.Group controlId="formBasicSelect">
				<Select
				      defaultValue={cityListWithLabel[0]}
				      label="Single select"
				      options={cityListWithLabel}
				      value={this.state.center}
				      onChange={this.onChangeCenter}
				    />
				    <label className={this.state.errorValues.center ? 'invalid-feedback': 'hide'}>{this.state.errorValues.center}</label>
		      	</Form.Group>

			<button className="main-button admit-button" onClick={()=>!this.state.admitStatus ? this.setVisibal() : this.asyncPDF(this.state)} style={{
			  color: theme.body,
			  backgroundColor: theme.text,
			  border: `solid 1px ${theme.text}`,
			}}>
			{this.state.buttonText} 
			</button>
	 	</form>
        </div>

        <div id="divToPrint" className={`repo-cards-div-main ${(!this.state.admitStatus  ? 'hide' :'')}`}>
            <AdmitCard theme={theme} contact={contactPageData} logo={greeting.logo_name} data={this.state.res.data} center={this.state.center}/>
        </div>
        <div className="project-button">
		<button className={`main-button admit-button ${(!this.state.admitStatus  ? 'hide' :'')}`} onClick={!this.state.admitStatus ? this.setVisibal : this.printDocument} style={{
		  color: theme.body,
		  backgroundColor: theme.text,
		  border: `solid 1px ${theme.text}`,
		}}>
		{this.state.buttonText} 
		</button>
        </div>
        <Footer theme={this.props.theme} onToggle={this.props.onToggle} />
        <TopButton theme={this.props.theme} />
      </div>
    );
  }
}

export default AdmitCardPage;
