import React, {Component, PropTypes} from 'react';
import html2canvas from 'html2canvas';
import jsPDF from 'jspdf';
import AdmitCard from "../../containers/education/AdmitCard";
import { contactPageData, greeting } from "../../portfolio";

class Createpdf extends Component {
  constructor(props) {
    super(props);
  }

  printDocument() {
    const input = document.getElementById('divToPrint');
    var doc = new jsPDF('p', 'pt','a4',true);
doc.setFontSize(16);
doc.setTextColor(80, 77, 78);
doc.text(15, 2, 'should be an image under here'); 
html2canvas(input, {
    useCORS : true,
    onrendered: function(canvas) {
       var imgData = canvas.toDataURL('image/jpeg');

       doc.addImage(imgData, 'JPEG', 15, 0, 34, 37);
       console.log(imgData);
       $('#prod').append(canvas);
       doc.save('Spec_Sheet.pdf');
    }
});
  }

  render() {
    return (
    <div>
      <div className="mt4">
        <div>Title of Component</div> 
        <div className="mb5">
      	  <button onClick={this.printDocument}>Print</button>
      	</div>
      </div>
      <div id="divToPrint" className="mt4">
        <div>Title of Component</div> 
        <div className="mb5">
      	  <AdmitCard contact={contactPageData} logo={greeting.logo_name}/>
      	</div>
      </div>
    </div>);
  }
}
export default Createpdf;
